floor_ops = instructions.map do |i|
  case i
  when '(' then 1
  when ')' then -1
  end
end

@first_neg = nil

floor = floor_ops.map.with_index(1).inject(0) do |sum, op_with_index|
  op, index = op_with_index
  current_floor = sum + op

  if current_floor < 0 && !@first_neg
    @first_neg = index
  end

  current_floor
end

puts floor
puts @first_neg